import { HttpClient } from '@angular/common/http';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { HttpTestingController } from '@angular/common/http/testing';
import { async, inject, TestBed } from '@angular/core/testing';
import { LogAnalyzerService } from './log-analyzer.service';
describe('LogAnalyzerService', () => {
    let service;
    let httpMock;
    let url = 'http://localhost:8080/logs';
    beforeEach(() => {
        TestBed.configureTestingModule({
            imports: [HttpClientTestingModule]
        });
        service = TestBed.inject(LogAnalyzerService);
    });
    it('should be created', () => {
        expect(service).toBeTruthy();
    });
    it(`should issue a request`, 
    // 1. declare as async test since the HttpClient works with Observables
    async(
    // 2. inject HttpClient and HttpTestingController into the test
    inject([HttpClient, HttpTestingController], (http, backend) => {
        // 3. send a simple request
        http.get('/info/INFO').subscribe();
        // 4. HttpTestingController supersedes `MockBackend` from the "old" Http package
        // here two, it's significantly less boilerplate code needed to verify an expected request
        backend.expectOne({
            url: '/info/INFO',
            method: 'GET'
        });
    })));
});
