import { HttpClientModule } from '@angular/common/http';
import { fakeAsync, TestBed, tick } from '@angular/core/testing';
import { RecipeComponent } from './recipe.component';
import { FilterRecipeService } from './services/filter-recipe.service';
import { ViewAllRecipeService } from './services/view-all-recipe.service';
describe('RecipeComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [HttpClientModule],
            declarations: [RecipeComponent],
            providers: [ViewAllRecipeService, FilterRecipeService
            ]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(RecipeComponent);
        const viewAllRecipeService = fixture.debugElement.injector.get(ViewAllRecipeService);
        const filterRecipeService = fixture.debugElement.injector.get(FilterRecipeService);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should', fakeAsync(() => {
        // spyOn(component, 'viewRecipes()');
        let button = fixture.debugElement.nativeElement.querySelector('button');
        button.click();
        tick();
        expect(component.viewRecipes()).toHaveBeenCalled();
    }));
});
