import { HttpClient } from '@angular/common/http';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { HttpTestingController } from '@angular/common/http/testing';
import { async, inject, TestBed } from '@angular/core/testing';
import { FilterRecipeService } from './filter-recipe.service';
describe('FilterRecipeService', () => {
    let filterRecipeService;
    let httpMock;
    let url = 'http://localhost:8080/recipes';
    beforeEach(() => {
        TestBed.configureTestingModule({
            imports: [HttpClientTestingModule]
        });
        filterRecipeService = TestBed.inject(FilterRecipeService);
    });
    it('should be created', () => {
        expect(filterRecipeService).toBeTruthy();
    });
    it('should call filterRecipes and return appropriate recipes', () => {
        const ingredient = 'mushroom';
        const recipes = [
            {
                "title": "Barbecued Grilled Portobello Sandwich",
                "href": "http://www.kraftfoods.com/kf/recipes/barbecued-grilled-portobello-sandwich-53215.aspx",
                "ingredients": [
                    "mushrooms",
                    "barbecue sauce",
                    "sandwich rolls",
                    "lettuce",
                    "tomato"
                ],
                "thumbnail": "http://img.recipepuppy.com/646777.jpg"
            },
            {
                "title": "Barbecued Grilled Portobello Sandwich",
                "href": "http://www.kraftfoods.com/kf/recipes/barbecued-grilled-portobello-sandwich-53215.aspx?cm_re=1-_-1-_-RecipeAlsoEnjoy",
                "ingredients": [
                    "mushrooms",
                    "barbecue sauce",
                    "sandwich rolls",
                    "lettuce",
                    "tomato"
                ],
                "thumbnail": "http://img.recipepuppy.com/675080.jpg"
            },
        ];
        filterRecipeService.filterRecipes(ingredient).subscribe((data) => {
            expect(data).toEqual(recipes);
        });
        async(inject([HttpClient, HttpTestingController], (http, backend) => {
            http.get('/recipes/ingredients?ingredients=ingredient').subscribe();
            backend.expectOne({
                url: '/recipes/ingredients?ingredients=mushroom',
                method: 'GET'
            });
        }));
    });
});
